import React, { useState } from "react";
import { EmploiList } from "../../data/emploi"; // Assurez-vous que le chemin est correct
import "./MesAnnonces.css"; // Assurez-vous que ce fichier existe

const MesAnnonces = () => {
  // Simuler l'employeur connecté via un état local
  const [connectedEmployerEmail, setConnectedEmployerEmail] = useState("rh@innovateurstech.com");

  const [emplois, setEmplois] = useState(EmploiList); // État pour gérer la liste des emplois

  const handleDelete = (emploiToDelete) => {
    setEmplois(emplois.filter((emploi) => emploi !== emploiToDelete)); // Supprime l'emploi de la liste
  };

  // Filtrer les emplois selon l'employeur connecté
  const filteredEmploiList = emplois.filter((emploi) => emploi.email_employeur === connectedEmployerEmail);

  const handleEmployerChange = (event) => {
    setConnectedEmployerEmail(event.target.value); // Met à jour l'employeur connecté
  };

  return (
    <div>
      <h2>Choisissez l'employeur connecté :</h2>
      <select onChange={handleEmployerChange} value={connectedEmployerEmail}>
        <option value="rh@innovateurstech.com">Innovateurs Tech</option>
        <option value="recrutement@solutionscreatives.ca">Solutions Créatives</option>
        <option value="contact@techverte.ca">Tech Verte</option>
        <option value="carrieres@nextgenia.com">NextGen IA</option>
        <option value="emplois@techsante.com">Tech Santé</option>
        <option value="hr@globalmedia.com">Global Media</option>
        <option value="contact@financesfuturistes.com">Finances Futuristes</option>
        <option value="info@biosolutions.com">BioSolutions</option>
        <option value="contact@energieverte.com">Énergie Verte</option>
        <option value="recrutement@servicesinnovants.com">Services Innovants</option>
      </select>

      <ul className="lmj-emploi-list">
        {filteredEmploiList.length > 0 ? (
          filteredEmploiList.map((emploi) => (
            <div className="emploi-container" key={emploi.nom_poste}>
              <h3 className="jobTitle">{emploi.nom_poste}</h3>
              <span className="jobEntreprise">{emploi.nom_entreprise}</span>
              <span className="jobSector">{emploi.categorie}</span>
              <span className="jobSalary">{emploi.salaire}</span>
              <span className="jobLocation">{emploi.emplacement}</span>
              <div className="button-container">
                <button className="buttonx" onClick={() => handleDelete(emploi)}>Supprimer</button>
              </div>
            </div>
          ))
        ) : (
          <li>Aucune offre d'emploi disponible.</li>
        )}
      </ul>
    </div>
  );
};

export default MesAnnonces;
