import React, { useState } from "react";
import "./ConnexionEnt.css";
import NavBar from '../NavBar/NavBar';
import { useNavigate } from 'react-router-dom';
import { EmployersList } from '../../data/employers';
import { useAuth } from '../AuthContext'; // Utilisation du contexte

function ConnexionEnt() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();
  const { login } = useAuth(); // Récupération de la fonction login du contexte

  const handleSubmit = (e) => {
    e.preventDefault();
    
    const employer = EmployersList.find(emp => emp.email === email && emp.password === password);

    if (employer) {
      login(email); // Enregistrer l'email dans le contexte
      navigate('/ent'); // Rediriger vers la page entreprise
    } else {
      setErrorMessage("Email ou mot de passe incorrect.");
    }
  };

  return (
    <div>
      <NavBar />
      <div className="connexion-container">
        <h2>Connexion</h2>
        <p>Employeur</p>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Entrez votre email"
            />
          </div>
          <div className="input-group">
            <label>Mot de passe</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Entrez votre mot de passe"
            />
          </div>
          <button className="login-btn" type="submit">Se connecter</button>
          {errorMessage && <div className="error-message">{errorMessage}</div>}
        </form>
        
        <div className="signup">
          <p>Pas de compte avec nous ?</p>
          <br />
          <a href="./inscent">
            <button className="signup-btn">Créer un compte</button>
          </a>
        </div>
      </div>
    </div>
  );
}

export default ConnexionEnt;
